// Listing.js
import React, { useState } from 'react';

function Listing() {
    const [savedLists, setSavedLists] = useState(() => {
        const saved = localStorage.getItem('savedLists');
        return saved ? JSON.parse(saved) : [];
    });

    const handleSave = () => {
        const listName = prompt("Enter a name for your list:");
        if (listName) {
            const newList = {
                name: listName,
                date: new Date().toLocaleDateString(),
                codes: savedLists.codes,
                images: savedLists.images
            };
            setSavedLists([...savedLists, newList]);
            localStorage.setItem('savedLists', JSON.stringify([...savedLists, newList]));
        }
    };

    return (
        <div>
            <h1>Saved Lists</h1>
            {savedLists.length > 0 ? (
                savedLists.map((list, index) => (
                    <div key={index}>
                        <h2>{list.name}</h2>
                        <p>{list.date}</p>
                        <div>
                            {list.images.map((src, imgIndex) => (
                                <img key={imgIndex} src={src} alt={`Response code ${imgIndex}`} />
                            ))}
                        </div>
                    </div>
                ))
            ) : (
                <p>No saved lists found.</p>
            )}
            <button onClick={handleSave}>Save Current List</button>
        </div>
    );
}

export default Listing;
